import React, { useState, useEffect } from 'react';
import api from './services/api';

import '../styles.css';

function FoodOrder(props){
    var [food, setFood] = useState([]);  
    useEffect (() => {
      async function loadFoods(){
        console.log("loading foods");        
        const response = await api.get('/foodDetail?'.concat(props));        
        food = response.data.result;
        setFood(food);
      }
  
      loadFoods();             
    }, []);    

    return (
        <>
        <p className="headline2">{food.food}</p>
        <div className="recipes_topic"> 
            <img src={food.img} width="72" height="72" alt="" />
            <p><span className="headline">{food.food}</span> <br />
            {food.description}
            <br/>
            Amount: {food.amount}
            </p>
            <div className="clear"></div>
        </div>
        <div>
            <label>Name</label>
            <input type="text" id="username" name="username"/>
        </div>
        <div>
            <label>Passphrase</label>
            <input type="text" id="passphrase" name="passphrase"/>
        </div>
        <div>
            <button type="submit" value='Order'></button>
        </div>
        </>
    );
}

export default FoodOrder;
