import React from 'react';
import { BrowserRouter, Link, Route, Switch } from 'react-router-dom';
import History from './components/History';
import Home from './components/Home';
import './styles.css';

//Componente: Bloco isolado de HTML, CSS e JS o qual não interfere no resto da aplicação. Nesse código está representado por uma função que renderiza algum HTML. (Regra de apenas 1 por arquivo)
//Propriedade: Informações que um componente PAI passa para o componente Filho. Ao utilizar propriedade função da propriedade utiliza (props) para passar propriedade
//Estado: Informações mantidas pelo componente (Lembrar: imutabilidade). Utilizar useState [variavel, funcaoParaAtualizarVariavel]

function App() {        
    
    return (
        <>
            <div id="header"> <img src="./images/logo.jpg" width="438" height="93" alt="" id="logo" /> <img src="./images/slogan.gif" width="190" height="81" alt="" id="slogan" />
                <ul class="menu">
                <li class="btn_1"><Link to={`/`}>home page</Link></li>
                <li class="line"></li>
                <li class="btn_2"><Link to={`/History`}>our history</Link></li>
                <li class="line"></li>
                <li class="btn_3"><Link to={`/Menu`}>menu</Link></li>      
                <li class="line"></li>
                <li class="btn_6"><Link to={`/Contacts`}>contacts</Link></li>
                </ul>
            </div>
            <div id="app">
                <div id="content">
                    <BrowserRouter>
                        <Route path="/" component={Home}/>
                        <Route path="/History" component={History}/>
                    </BrowserRouter>
                </div>            
            </div>
        </>
      );
    }
    
    export default App;
