import React from 'react';
import ReactDOM from 'react-dom';
import { NavLink, Switch, Route, BrowserRouter as Router } from 'react-router-dom';
import FoodOrder from './FoodOrder';

import '../styles.css';

function FoodItemDetail(props){
    const { food } = props;

    this.handleClick = this.handleClick.bind(food.foodType, this);

    function handleClick(foodType,e){
        e.preventDefault();
    
        alert('clicked');
    
        foodRouting(foodType);
    }
    
    function foodRouting(foodType){
        const routing = (
            <Router>                                
                    <Route path={`/FoodOrder/${foodType}`} key={foodType} component={FoodOrder}></Route>
            </Router>
        );
    
        ReactDOM.render(routing, document.getElementById('root'));
    }

    return (        
        <>
            <p className="headline2">{food.food}</p>

            <a onClick={this.handleClick(food.foodType)}>
                <div className="recipes_topic"> 
                    <img src={food.img} width="72" height="72" alt="" />
                    <p><span className="headline">{food.food}</span> <br />
                    {food.description}</p>
                    <div className="clear"></div>
                </div>
            </a>
       </>                         
    );
}

export default FoodItemDetail;
