import React, { useState, useEffect, Component } from 'react';
import api from '../services/api';
import '../styles.css';
const FoodTransaction = require( "../FoodTransaction");

const cryptography = require('@liskhq/lisk-cryptography');
const transaction = require("@liskhq/lisk-transactions");

class FormPayment extends Component{
    constructor (props){        
        super(props);

        this.state = {
            passphrase: '',
            orderstring: props.orderstring,
            response: { meta: { status: false }},
        }
    }
    
    handleChange = (event) => {
        let name = event.target.name;
        let val = event.target.value;
        this.setState({[name]: val});
    };       
    
    /*onSubmit = async (transaction) =>
    {
        console.log(transaction);
        let order = await api.post('/clientPayment', {transaction: transaction, networkid: 'identifier'});
        console.log(order);        
    }*/

    handleSubmit = async (e) => {
        e.preventDefault();
                
        if (this.state.orderstring !== null && this.state.orderstring !== undefined){    
            this.state.orderstring = this.state.orderstring.split("&");                                      
        }

        const username = await api.post("/cryptography", { text: this.state.orderstring[5].split('=')[1] });
        const phone = await api.post("/cryptography", { text: this.state.orderstring[6].split('=')[1] });
        const deliveryaddress = await api.post("/cryptography", { text: this.state.orderstring[7].split('=')[1] });
        let observation = "";
        if (this.state.orderstring[8] !== undefined){
            observation = await api.post("/cryptography", { text: this.state.orderstring[8].split('=')[1] });
        }        
        
        const publicKey = cryptography.getPrivateAndPublicKeyFromPassphrase(this.state.passphrase).publicKey;

        /*const clientData = cryptography.encryptMessageWithPassphrase(
            this.state.orderstring[5].split('=')[1].concat(' ***Field*** ')
            .concat(this.state.orderstring[6].split('=')[1]).concat(' ***Field*** ')
            .concat(this.state.orderstring[7].split('=')[1]).concat(' ***Field*** ')
            .concat(this.state.orderstring[8].split('=')[1]),
            this.state.passphrase,
            publicKey
            );   */                         

        const networkIdentifier = cryptography.getNetworkIdentifier(
            "23ce0366ef0a14a91e5fd4b1591fc880ffbef9d988ff8bebf8f3666b0c09597d",
            "Lisk",
        );       

        //const encryptedclient= cryptography.encryptMessageWithPassphrase(passphrase, passphrase, orderstring[9].split('=')[1]);        
        
        const txFood = new FoodTransaction({
            asset: {
                /*name: this.state.orderstring[2].split('=')[1],
                description: this.state.orderstring[2].split('=')[1],
                username: username.data.response,
                phone: phone.data.response,
                deliveryaddress: deliveryaddress.data.response,
                foodType: this.state.orderstring[3].split('=')[1],
                observation: observation.data !== undefined ? observation.data.response : "",
                clientData: clientData.encryptedMessage,
                clientNonce: clientData.nonce,*/
                amount: this.state.orderstring[1].split('=')[1].toString(),
                recipientId: this.state.orderstring[0].split('=')[1], //restaurant lisk address
                //key: encryptedclient.encryptedMessage,
                //keynonce: encryptedclient.nonce,
                //clientpublickey: cryptography.getAddressAndPublicKeyFromPassphrase(this.state.passphrase).publicKey
            },                
            timestamp: transaction.utils.getTimeFromBlockchainEpoch(new Date()),
            networkIdentifier: networkIdentifier
        });             

        txFood.sign(this.state.passphrase);                

        alert(txFood);
        console.log(txFood);        

        await onSubmit(txFood);                       
    }    

    render() {
    return (
        <div id="app">
            <div id="content" align="center">                
                <form method="Post" onSubmit={this.handleSubmit}>                                  
                    <div>
                        <label>Your Lisk Passphrase</label>
                    </div>
                    <div>
                        <input type="password" id="passphrase" name="passphrase" required onChange={this.handleChange}/>
                    </div>
                    <div>
                        <label>copy this passphrase and test above if desired: creek own stem final gate scrub live shallow stage host concert they</label>
                    </div>
                    <div>
                        <button type="submit">Order</button>
                    </div>
                </form>
            </div>
        </div>
    );
    }
}

export default FormPayment;
