import React, { useState, useEffect } from 'react';
import api from '../services/api';

import '../styles.css';

function FoodOrder(props){        
    var foodType = document.location.pathname.split("/")[2];
    console.log(foodType);

    var [food, setFood] = useState([]);  
    useEffect (() => {
      async function loadFood(){
        console.log("loading food");                    
        const response = await api.get('/foodDetail/'.concat(foodType));        
        food = response.data.result;
        setFood(food);
      }
        
      loadFood();             
    }, []);    

    /*async function handleSubmit(e){
        e.preventDefault();

        await onsubmit({

        });

    }*/

    return 
        (
        <>
        <p className="headline2">{food.food}</p>
        <div className="recipes_topic"> 
            <img src={food.img} width="72" height="72" alt="" />
            <p><span className="headline">{food.food}</span> <br />
            {food.description}
            <br/>
            Amount: {food.amount}
            </p>
            <div className="clear"></div>
        </div>
        <form >
            <input type="hidden" id="request_type" name="request_type"></input>
            <div>
                <label>Name</label>
                <input type="text" id="username" name="username" required/>
            </div>
            <div>
                <label>Passphrase</label>
                <input type="text" id="passphrase" name="passphrase" required/>
            </div>
            <div>
                <button type="submit" value='Order'></button>
            </div>
        </form>
        </>
    );
}

export default FoodOrder;
